mapper.select:
select menu_id, name, permission, level, pid, created_time, update_time, is_enabled from sys_menu
where is_deleted = 0

    and name like concat('%', #{params.name}, '%')


    and level = #{params.level}


    and pid = #{params.pid}


    and created_time between #{params.menuTime[0],jdbcType=TIMESTAMP} and #{params.menuTime[1],jdbcType=TIMESTAMP}


    order by ${sortColumn} ${sortMethod}

limit #{index}, #{pageSize}

select count(*)
from sys_menu
where is_deleted = 0

    and name like concat('%', #{params.name}, '%')


    and level = #{params.level}


    and created_time between #{params.menuTime[0],jdbcType=TIMESTAMP} and #{params.menuTime[1],jdbcType=TIMESTAMP}
