mapper.select:
select user_id, username, phone, mail, header, signature, description, realname, address, created_time,
update_time, is_enabled from sys_user
where is_deleted = 0

    and username like concat('%', #{params.username}, '%')


    and created_time between #{params.userTime[0],jdbcType=TIMESTAMP} and #{params.userTime[1],jdbcType=TIMESTAMP}


    order by ${sortColumn} ${sortMethod}

limit #{index}, #{pageSize}

select count(*) from sys_user where is_deleted = 0
and username like concat('%', #{params.username}, '%')
and created_time between #{params.userTime[0],jdbcType=TIMESTAMP} and #{params.userTime[1],jdbcType=TIMESTAMP}
